    def on_register_button_clicked(self):
        self.parent.setCurrentWidget(self.parent.widget(1))

    def on_login_button_clicked(self):
        user = User.login(self.email_text_field.text(), self.password_textfield.text()) 
        if(user):
            if(user.user_type == User.USER):
                home = HomePage(parent=self.parent, user =user)
                
            else:
                home = AdminHomePage(parent=self.parent, user=user)
            self.parent.addWidget(home)
            self.parent.setCurrentWidget(home)
            print(user.user_type)

        else:
            dialog = QtWidgets.QMessageBox()
            dialog.setIcon(QtWidgets.QMessageBox.Critical)
            dialog.setWindowTitle("Login Error")
            dialog.setText("Invalid email or password")
            dialog.setStandardButtons(QtWidgets.QMessageBox.Ok)
            dialog.exec_()
            
        print(user)
        
        
